        // Use Anvil's default test account
        const wallet = new ethers.Wallet("0xac0974bec39a17e36ba4a6b4d238ff944bacb478cbed5efcae784d7bf4f2ff80", provider);
        
        console.log(`üë§ Using wallet: ${wallet.address}`);
        
